sys_script_include.script:
/**
 * Provides helper functions for use in script_includes
 * @type {Object}
 */
var xMattersUtil = Class.create();
xMattersUtil.prototype = {
    initialize: function() {
    },

	/**
    * Given an array of supervisor JSON objects, fetch the list of supervisor UUIDs
    * @param {Array} supervisors array of objects for supervisors
    * @returns {Array} of supervisor UUIDs
    */
    getSupervisorIds: function (supervisors) {
        var supervisorIds = [];
        for (var i = 0; i < supervisors.length; i++) {
            supervisorIds.push(supervisors[i].id);
        }
        return supervisorIds;
    },

    type: 'xMattersUtil'
};
