sysevent_script_action.script:
gs.include("xMattersTask");
gs.include("xMattersLogger");

// The Engage inserted script action has been modified to handle both inserts and updates
// it was not renamed in order to limit the amount of possible conflicts for customers
// migrating to the new version.
// Logic is based on the properties given to event.parm1 object, and implemented in xMattersTask handleTaskAction
var appPrefix = gs.getCurrentScopeName();
var log = new xMattersLogger(gs.getProperty(appPrefix + '.' + "xmatters.logging.level"), 'Script Action Engage Inserted');

if (!gs.nil(event.parm1) &&
	!gs.nil(event.parm2) && (
		event.parm2.toString() == 'taskInsert' || event.parm2.toString() == 'terminateEvent'
	)) {
	log.debug('Entering script action with parm1: ' + event.parm1.toString());
	new xMattersTask().handleTaskAction(current, new global.JSON().decode(event.parm1));
} else {
	log.debug("Entering xMatters Engage Inserted script action with null/empty parm1");
}
