sysevent_script_action.script:
gs.include("xMattersRequest2");
gs.include("xMattersLogger");

var appPrefix = gs.getCurrentScopeName();
var log = new xMattersLogger(gs.getProperty(appPrefix + '.' + "xmatters.logging.level"), 'Script Action Request Updated' );

if ( !gs.nil( event.parm1 ) && !gs.nil( event.parm2 ) &&
	( event.parm2.toString() == "requestUpdate" || event.parm2.toString() == "requestInsert" ) ) {

	log.debug( "Entering script action with parm1: " + event.parm1.toString() );
	new xMattersRequest2().handleRequestAction(current, new global.JSON().decode( event.parm1 ));

} else {
	log.debug( "Entering xMatters Request Updated script action with null/empty or invalid parm1" );
}
